function getPreviewURL(url) {
	url = url.trim();
	if (url.length > 0){
		try {
			window.open(resolvePreviewURL(url), null, "height=400,width=400,status=yes,toolbar=no,menubar=no,resizable=yes,scrollbars=yes,location=no");			
		}
		catch(ex)
		{
			alert(ex.message);
		}
	}
}

function resolvePreviewURL(url) {
	if (url.indexOf("/") == 0) {
		return url;
	}
	if (url.indexOf(".") == 0) {
		return url;
	}
	if (url.indexOf("://") > 0) {
		return url;
	}
	if (url.indexOf(":\\") > 0) {
		return url;
	}

	return "http://" + url;
}

